def get_response_by_intent(intent):
    phrases = BOT_CONFIG['intents'][intent]['responses']
    return random.choice(phrases)


def get_phailure_phrase():
    phrases = BOT_CONFIG['failure_phrases']
    return random.choice(phrases)


