def go_bot(text):
    """Генерация ответной реплики"""
    # NLU
    intent = get_intent(text)

    # Generate answer
    
    # rules
    if intent:
        stats['intent'] += 1
        return get_response_by_intent(intent)

    # use generative model
    response = get_generative_response(text)
    if response:
        stats['generative'] += 1
        return response

    # stub
    stats['stub'] += 1
    return get_phailure_phrase()


